<template>
  <div class="flex flex-col gap-4">
    <input type="file" @change="onFileSelect" />
    <p v-if="fileName">üìÑ Wybrano: <strong>{{ fileName }}</strong></p>
    <button v-if="file" class="btn btn-primary w-fit" @click="uploadFile">Upload</button>
    <p v-if="status" :class="statusClass">{{ status }}</p>
  </div>
</template>

<script setup>
import { ref, computed } from 'vue'
import axios from 'axios'

const file = ref(null)
const fileName = ref('')
const status = ref('')

const statusClass = computed(() => {
  if (status.value.startsWith('‚úÖ')) return 'text-green-600'
  if (status.value.startsWith('‚ùå')) return 'text-red-600'
  return ''
})

// Wklej wygenerowany Signed URL
const signedUrl = 'https://storage.googleapis.com/project-storege/upload-1750171384462.jpg?X-Goog-Algorithm=GOOG4-RSA-SHA256&X-Goog-Credential=signed-url-service%40meals-finders.iam.gserviceaccount.com%2F20250617%2Fauto%2Fstorage%2Fgoog4_request&X-Goog-Date=20250617T144304Z&X-Goog-Expires=604800&X-Goog-SignedHeaders=content-type%3Bhost&X-Goog-'

function onFileSelect(event) {
  file.value = event.target.files[0]
  fileName.value = file.value?.name || ''
}

async function uploadFile() {
  if (!file.value) return

  try {
    await axios.put(signedUrl, file.value, {
      headers: { 'Content-Type': 'application/octet-stream' },
    })
    status.value = '‚úÖ Plik wys≈Çany pomy≈õlnie!'
  } catch (err) {
    console.error(err)
    status.value = '‚ùå B≈ÇƒÖd uploadu: ' + err.message
  }
}
</script>
